CREATE DATABASE `elections`
CREATE TABLE `elections`.`voters` ( `id` INT NOT NULL AUTO_INCREMENT , `pass` VARCHAR(200) NOT NULL DEFAULT 'Didn\'t Vote', `bhavan` VARCHAR(200) NOT NULL DEFAULT 'Didn\'t Vote', `president` VARCHAR(200) NOT NULL DEFAULT 'Didn\'t Vote', `gensec` VARCHAR(200) NOT NULL DEFAULT 'Didn\'t Vote', `cultsec` VARCHAR(200) NOT NULL DEFAULT 'Didn\'t Vote', `sportssec` VARCHAR(200) NOT NULL DEFAULT 'Didn\'t Vote', `smc` VARCHAR(200) NOT NULL DEFAULT 'Didn\'t Vote', PRIMARY KEY (`id`)) ENGINE = InnoDB;
